namespace Kaitai\Struct\Tests;

class ExprBitsTest extends TestCase {
    public function testExprBits() {
        $r = ExprBits::fromFile(self::SRC_DIR_PATH . '/switch_opcodes.bin');

        $this->assertSame(2, $r->a());
        $this->assertSame(\Kaitai\Struct\Tests\ExprBits\Items::FOO, $r->enumSeq());
        $this->assertSame("\x66\x6F", $r->byteSize());
        $this->assertSame(2, count($r->repeatExpr()));
        $this->assertSame(111, $r->repeatExpr()[0]);
        $this->assertSame(98, $r->repeatExpr()[1]);
        $this->assertSame(97, $r->switchOnType());
        $this->assertSame(29184, $r->switchOnEndian()->foo());
        $this->assertSame(\Kaitai\Struct\Tests\ExprBits\Items::BAR, $r->enumInst());
        $this->assertSame(111, $r->instPos());
    }
}
