namespace Kaitai\Struct\Tests;

class InstanceStdArrayTest extends TestCase {
    public function testInstanceStdArray() {
        $r = InstanceStdArray::fromFile(self::SRC_DIR_PATH . '/instance_std_array.bin');

        $this->assertSame(16, $r->ofs());
        $this->assertSame(3, $r->qtyEntries());
        $this->assertSame(4, $r->entrySize());
        $this->assertSame(3, count($r->entries()));
        $this->assertSame("\x11\x11\x11\x11", $r->entries()[0]);
        $this->assertSame("\x22\x22\x22\x22", $r->entries()[1]);
        $this->assertSame("\x33\x33\x33\x33", $r->entries()[2]);
    }
}
