namespace Kaitai\Struct\Tests;

class IntegersDoubleOverflowTest extends TestCase {
    public function testIntegersDoubleOverflow() {
        $r = IntegersDoubleOverflow::fromFile(self::SRC_DIR_PATH . '/integers_double_overflow.bin');

        $this->assertSame(-9007199254740991, $r->signedSafeMinBe());
        $this->assertSame(-9007199254740991, $r->signedSafeMinLe());
        $this->assertSame(9007199254740991, $r->signedSafeMaxBe());
        $this->assertSame(9007199254740991, $r->signedSafeMaxLe());
        $this->assertSame("-9007199254740993", strval($r->signedUnsafeNegBe()));
        $this->assertSame("-9007199254740993", strval($r->signedUnsafeNegLe()));
        $this->assertSame("9007199254740993", strval($r->signedUnsafePosBe()));
        $this->assertSame("9007199254740993", strval($r->signedUnsafePosBe()));
    }
}
