namespace Kaitai\Struct\Tests;

class StrEncodingsUtf16Test extends TestCase {
    public function testStrEncodingsUtf16() {
        $r = StrEncodingsUtf16::fromFile(self::SRC_DIR_PATH . '/str_encodings_utf16.bin');

        $this->assertSame(12, $r->lenBe());
        $this->assertSame(65279, $r->beBomRemoved()->bom());
        $this->assertSame("\u{3053}\u{3093}\u{306b}\u{3061}\u{306f}", $r->beBomRemoved()->str());
        $this->assertSame(12, $r->lenLe());
        $this->assertSame(65279, $r->leBomRemoved()->bom());
        $this->assertSame("\u{3053}\u{3093}\u{306b}\u{3061}\u{306f}", $r->leBomRemoved()->str());
    }
}
