namespace Kaitai\Struct\Tests;

class SwitchBytearrayTest extends TestCase {
    public function testSwitchBytearray() {
        $r = SwitchBytearray::fromFile(self::SRC_DIR_PATH . '/switch_opcodes.bin');

        $this->assertSame(4, count($r->opcodes()));
        $this->assertSame("\x53", $r->opcodes()[0]->code());
        $this->assertSame("foobar", $r->opcodes()[0]->body()->value());
        $this->assertSame("\x49", $r->opcodes()[1]->code());
        $this->assertSame(66, $r->opcodes()[1]->body()->value());
        $this->assertSame("\x49", $r->opcodes()[2]->code());
        $this->assertSame(55, $r->opcodes()[2]->body()->value());
        $this->assertSame("\x53", $r->opcodes()[3]->code());
        $this->assertSame("", $r->opcodes()[3]->body()->value());
    }
}
